mapper.select:
select 
b.bno, b.title, b.cre_dt, b.cate, b.likes, b.views, m.nick
from boards b left outer join members m 
on m.mno = b.mno
where 

  b.cate=#{category}
  
    
       and b.title like concat ('%', #{title}, '%') 
       and m.nick=#{nickname} 
    
  


  
    b.title like concat ('%', #{title}, '%') 
    m.nick=#{nickname} 
   

order by b.bno desc
limit #{startIndex}, #{length}

select count(*) from boards b left outer join members m
on m.mno = b.mno
where

  b.cate=#{category}
  
    
       and b.title like concat ('%', #{title}, '%') 
       and m.nick=#{nickname} 
    
  


  
    b.title like concat ('%', #{title}, '%') 
    m.nick=#{nickname}
